import{_ as p,o as a,c as e,e as t}from"./app.6d5982fd.js";const r={},o=t('<div class="heti heti--ancient"><h2 id="\u6CB3\u4F2F\u4E0E\u5317\u6D77\u82E5" tabindex="-1">\u6CB3\u4F2F\u4E0E\u5317\u6D77\u82E5 <a class="header-anchor" href="#\u6CB3\u4F2F\u4E0E\u5317\u6D77\u82E5" aria-label="Permalink to &quot;\u6CB3\u4F2F\u4E0E\u5317\u6D77\u82E5&quot;">\u200B</a></h2><p>\u79CB\u6C34\u65F6\u81F3\uFF0C\u767E\u5DDD\u704C\u6CB3\uFF1B\u6CFE\u6D41\u4E4B\u5927\uFF0C\u4E24\u6D98\u6E1A\u5D16\u4E4B\u95F4\u4E0D\u8FA9\u725B\u9A6C\u3002\u4E8E\u662F\u7109\u6CB3\u4F2F\u6B23\u7136\u81EA\u559C\uFF0C\u4EE5\u5929\u4E0B\u4E4B\u7F8E\u4E3A\u5C3D\u5728\u5DF1\u3002\u987A\u6D41\u800C\u4E1C\u884C\uFF0C\u81F3\u4E8E\u5317\u6D77\uFF0C\u4E1C\u9762\u800C\u89C6\uFF0C\u4E0D\u89C1\u6C34\u7AEF\u3002\u4E8E\u662F\u7109\u6CB3\u4F2F\u59CB\u65CB\u5176\u9762\u76EE\uFF0C\u671B\u6D0B\u5411\u82E5\u800C\u53F9\u66F0\uFF1A\u201C\u91CE\u8BED\u6709\u4E4B\u66F0\uFF1A\u2018\u95FB\u9053\u767E\uFF0C\u4EE5\u4E3A\u83AB\u5DF1\u82E5\u8005\u3002\u2019\u6211\u4E4B\u8C13\u4E5F\u3002\u4E14\u592B\u6211\u5C1D\u95FB\u5C11\u4EF2\u5C3C\u4E4B\u95FB\uFF0C\u800C\u8F7B\u4F2F\u5937\u4E4B\u4E49\u8005\uFF0C\u59CB\u543E\u5F17\u4FE1\uFF0C\u4ECA\u6211\u7779\u5B50\u4E4B\u96BE\u7A77\u4E5F\uFF0C\u543E\u975E\u81F3\u4E8E\u5B50\u4E4B\u95E8\u5219\u6B86\u77E3\u3002\u543E\u957F\u89C1\u7B11\u4E8E\u5927\u65B9\u4E4B\u5BB6\u3002\u201D</p><p>\u5317\u6D77\u82E5\u66F0\uFF1A\u201C\u4E95\u9F03\u4E0D\u53EF\u4EE5\u8BED\u4E8E\u6D77\u8005\uFF0C\u62D8\u4E8E\u865A\u4E5F\uFF1B\u590F\u866B\u4E0D\u53EF\u4EE5\u8BED\u4E8E\u51B0\u8005\uFF0C\u7B03\u4E8E\u65F6\u4E5F\uFF1B\u66F2\u58EB\u4E0D\u53EF\u4EE5\u8BED\u4E8E\u9053\u8005\uFF0C\u675F\u4E8E\u6559\u4E5F\u3002\u4ECA\u5C14\u51FA\u4E8E\u5D16\u6D98\uFF0C\u89C2\u4E8E\u5927\u6D77\uFF0C\u4E43\u77E5\u5C14\u4E11\uFF0C\u5C14\u5C06\u53EF\u4E0E\u8BED\u5927\u7406\u77E3\u3002\u5929\u4E0B\u4E4B\u6C34\uFF0C\u83AB\u5927\u4E8E\u6D77\u3002\u4E07\u5DDD\u5F52\u4E4B\uFF0C\u4E0D\u77E5\u4F55\u65F6\u6B62\u800C\u4E0D\u76C8\uFF1B\u5C3E\u95FE\u6CC4\u4E4B\uFF0C\u4E0D\u77E5\u4F55\u65F6\u5DF2\u800C\u4E0D\u865A\uFF1B\u6625\u79CB\u4E0D\u53D8\uFF0C\u6C34\u65F1\u4E0D\u77E5\u3002\u6B64\u5176\u8FC7\u6C5F\u6CB3\u4E4B\u6D41\uFF0C\u4E0D\u53EF\u4E3A\u91CF\u6570\u3002\u800C\u543E\u672A\u5C1D\u4EE5\u6B64\u81EA\u591A\u8005\uFF0C\u81EA\u4EE5\u6BD4\u5F62\u4E8E\u5929\u5730\uFF0C\u800C\u53D7\u6C14\u4E8E\u9634\u9633\uFF0C\u543E\u5728\u5929\u5730\u4E4B\u95F4\uFF0C\u72B9\u5C0F\u77F3\u5C0F\u6728\u4E4B\u5728\u5927\u5C71\u4E5F\u3002\u65B9\u5B58\u4E4E\u89C1\u5C11\uFF0C\u53C8\u595A\u4EE5\u81EA\u591A\uFF01\u8BA1\u56DB\u6D77\u4E4B\u5728\u5929\u5730\u4E4B\u95F4\u4E5F\uFF0C\u4E0D\u4F3C\u7928(l\u011Bi)\u7A7A\u4E4B\u5728\u5927\u6CFD\u4E4E\uFF1F\u8BA1\u4E2D\u56FD\u4E4B\u5728\u6D77\u5185\uFF0C\u4E0D\u4F3C\u7A0A\u7C73\u4E4B\u5728\u5927\uFF08t\xE0i\uFF09\u4ED3\u4E4E\uFF1F\u53F7\u7269\u4E4B\u6570\u8C13\u4E4B\u4E07\uFF0C\u4EBA\u5904\u4E00\u7109\uFF1B\u4EBA\u5352\u4E5D\u5DDE\uFF0C\u8C37\u98DF\u4E4B\u6240\u751F\uFF0C\u821F\u8F66\u4E4B\u6240\u901A\uFF0C\u4EBA\u5904\u4E00\u7109\u3002\u6B64\u5176\u6BD4\u4E07\u7269\u4E5F\uFF0C\u4E0D\u4F3C\u8C6A\u672B\u4E4B\u5728\u4E8E\u9A6C\u4F53\u4E4E\uFF1F\u4E94\u5E1D\u4E4B\u6240\u8FDE\uFF0C\u4E09\u738B\u4E4B\u6240\u4E89\uFF0C\u4EC1\u4EBA\u4E4B\u6240\u5FE7\uFF0C\u4EFB\u58EB\u4E4B\u6240\u52B3\uFF0C\u5C3D\u6B64\u77E3\uFF01\u4F2F\u5937\u8F9E\u4E4B\u4EE5\u4E3A\u540D\uFF0C\u4EF2\u5C3C\u8BED\u4E4B\u4EE5\u4E3A\u535A\u3002\u6B64\u5176\u81EA\u591A\u4E5F\uFF0C\u4E0D\u4F3C\u5C14\u5411\u4E4B\u81EA\u591A\u4E8E\u6C34\u4E4E\uFF1F\u201D</p><p>\u6CB3\u4F2F\u66F0\uFF1A\u201C\u7136\u5219\u543E\u5927\u5929\u5730\u800C\u5C0F\u8C6A\u672B\uFF0C\u53EF\u4E4E\uFF1F\u201D</p><p>\u5317\u6D77\u82E5\u66F0\u201C\u5426\u3002\u592B\u7269\uFF0C\u91CF\u65E0\u7A77\uFF0C\u65F6\u65E0\u6B62\uFF0C\u5206\u65E0\u5E38\uFF0C\u7EC8\u59CB\u65E0\u6545\u3002\u662F\u6545\u5927\u77E5\u89C2\u4E8E\u8FDC\u8FD1\uFF0C\u6545\u5C0F\u800C\u4E0D\u5BE1\uFF0C\u5927\u800C\u4E0D\u591A\uFF1A\u77E5\u91CF\u65E0\u7A77\u3002\u8BC1\u5411\u4ECA\u6545\uFF0C\u6545\u9065\u800C\u4E0D\u95F7\uFF0C\u6387\u800C\u4E0D\u8DC2\uFF1A\u77E5\u65F6\u65E0\u6B62\u3002\u5BDF\u4E4E\u76C8\u865A\uFF0C\u6545\u5F97\u800C\u4E0D\u559C\uFF0C\u5931\u800C\u4E0D\u5FE7\uFF1A\u77E5\u5206\u4E4B\u65E0\u5E38\u4E5F\u3002\u660E\u4E4E\u5766\u6D82\uFF0C\u6545\u751F\u800C\u4E0D\u8BF4\uFF0C\u6B7B\u800C\u4E0D\u7978\uFF1A\u77E5\u7EC8\u59CB\u4E4B\u4E0D\u53EF\u6545\u4E5F\u3002\u8BA1\u4EBA\u4E4B\u6240\u77E5\uFF0C\u4E0D\u82E5\u5176\u6240\u4E0D\u77E5\uFF1B\u5176\u751F\u4E4B\u65F6\uFF0C\u4E0D\u82E5\u672A\u751F\u4E4B\u65F6\uFF1B\u4EE5\u5176\u81F3\u5C0F\uFF0C\u6C42\u7A77\u5176\u81F3\u5927\u4E4B\u57DF\uFF0C\u662F\u6545\u8FF7\u4E71\u800C\u4E0D\u80FD\u81EA\u5F97\u4E5F\u3002\u7531\u6B64\u89C2\u4E4B\uFF0C\u53C8\u4F55\u4EE5\u77E5\u6BEB\u672B\u4E4B\u8DB3\u4EE5\u5B9A\u81F3\u7EC6\u4E4B\u502A\uFF0C\u53C8\u4F55\u4EE5\u77E5\u5929\u5730\u4E4B\u8DB3\u4EE5\u7A77\u81F3\u5927\u4E4B\u57DF\uFF01\u201D</p><p>\u6CB3\u4F2F\u66F0\uFF1A\u201C\u4E16\u4E4B\u8BAE\u8005\u7686\u66F0\uFF1A\u2018\u81F3\u7CBE\u65E0\u5F62\uFF0C\u81F3\u5927\u4E0D\u53EF\u56F4\u3002\u2019\u662F\u4FE1\u60C5\u4E4E\uFF1F\u201D</p><p>\u5317\u6D77\u82E5\u66F0\uFF1A\u201C\u592B\u81EA\u7EC6\u89C6\u5927\u8005\u4E0D\u5C3D\uFF0C\u81EA\u5927\u89C6\u7EC6\u8005\u4E0D\u660E\u3002\u592B\u7CBE\uFF0C\u5C0F\u4E4B\u5FAE\u4E5F\uFF1B\u57BA\uFF0C\u5927\u4E4B\u6BB7\u4E5F\uFF1A\u6545\u5F02\u4FBF\u3002\u6B64\u52BF\u4E4B\u6709\u4E5F\u3002\u592B\u7CBE\u7C97\u8005\uFF0C\u671F\u4E8E\u6709\u5F62\u8005\u4E5F\uFF1B\u65E0\u5F62\u8005\uFF0C\u6570\u4E4B\u6240\u4E0D\u80FD\u5206\u4E5F\uFF1B\u4E0D\u53EF\u56F4\u8005\uFF0C\u6570\u4E4B\u6240\u4E0D\u80FD\u7A77\u4E5F\u3002\u53EF\u4EE5\u8A00\u8BBA\u8005\uFF0C\u7269\u4E4B\u7C97\u4E5F\uFF1B\u53EF\u4EE5\u610F\u81F4\u8005\uFF0C\u7269\u4E4B\u7CBE\u4E5F\uFF1B\u8A00\u4E4B\u6240\u4E0D\u80FD\u8BBA\uFF0C\u610F\u4E4B\u6240\u4E0D\u80FD\u5BDF\u81F4\u8005\uFF0C\u4E0D\u671F\u7CBE\u7C97\u7109\u3002\u662F\u6545\u5927\u4EBA\u4E4B\u884C\uFF1A\u4E0D\u51FA\u4E4E\u5BB3\u4EBA\uFF0C\u4E0D\u591A\u4EC1\u6069\uFF1B\u52A8\u4E0D\u4E3A\u5229\uFF0C\u4E0D\u8D31\u95E8\u96B6\uFF1B\u8D27\u8D22\u5F17\u4E89\uFF0C\u4E0D\u591A\u8F9E\u8BA9\uFF1B\u4E8B\u7109\u4E0D\u501F\u4EBA\uFF0C\u4E0D\u591A\u98DF\u4E4E\u529B\uFF0C\u4E0D\u8D31\u8D2A\u6C61\uFF1B\u884C\u6B8A\u4E4E\u4FD7\uFF0C\u4E0D\u591A\u8F9F\u5F02\uFF1B\u4E3A\u5728\u4ECE\u4F17\uFF0C\u4E0D\u8D31\u4F5E\u8C04\uFF1B\u4E16\u4E4B\u7235\u7984\u4E0D\u8DB3\u4EE5\u4E3A\u529D\uFF0C\u622E\u803B\u4E0D\u8DB3\u4EE5\u4E3A\u8FB1\uFF1B\u77E5\u662F\u975E\u4E4B\u4E0D\u53EF\u4E3A\u5206\uFF0C\u7EC6\u5927\u4E4B\u4E0D\u53EF\u4E3A\u502A\u3002\u95FB\u66F0\uFF1A\u2018\u9053\u4EBA\u4E0D\u95FB\uFF0C\u81F3\u5FB7\u4E0D\u5F97\uFF0C\u5927\u4EBA\u65E0\u5DF1\u3002\u2019\u7EA6\u5206\u4E4B\u81F3\u4E5F\u3002\u201D</p><p>\u6CB3\u4F2F\u66F0\uFF1A\u201C\u82E5\u7269\u4E4B\u5916\uFF0C\u82E5\u7269\u4E4B\u5185\uFF0C\u6076\u81F3\u800C\u502A\u8D35\u8D31\uFF1F\u6076\u81F3\u800C\u502A\uFF08n\xED\uFF09\u5C0F\u5927\uFF1F\u201D</p><p>\u5317\u6D77\u82E5\u66F0\uFF1A\u201C\u4EE5\u9053\u89C2\u4E4B\uFF0C\u7269\u65E0\u8D35\u8D31\uFF1B\u4EE5\u7269\u89C2\u4E4B\uFF0C\u81EA\u8D35\u800C\u76F8\u8D31\uFF1B\u4EE5\u4FD7\u89C2\u4E4B\uFF0C\u8D35\u8D31\u4E0D\u5728\u5DF1\u3002\u4EE5\u5DEE\u89C2\u4E4B\uFF0C\u56E0\u5176\u6240\u5927\u800C\u5927\u4E4B\uFF0C\u5219\u4E07\u7269\u83AB\u4E0D\u5927\uFF1B\u56E0\u5176\u6240\u5C0F\u800C\u5C0F\u4E4B\uFF0C\u5219\u4E07\u7269\u83AB\u4E0D\u5C0F\u3002\u77E5\u5929\u5730\u4E4B\u4E3A\u7A0A\u7C73\u4E5F\uFF0C\u77E5\u6BEB\u672B\u4E4B\u4E3A\u4E18\u5C71\u4E5F\uFF0C\u5219\u5DEE\u6570\u7779\u77E3\u3002\u4EE5\u529F\u89C2\u4E4B\uFF0C\u56E0\u5176\u6240\u6709\u800C\u6709\u4E4B\uFF0C\u5219\u4E07\u7269\u83AB\u4E0D\u6709\uFF1B\u56E0\u5176\u6240\u65E0\u800C\u65E0\u4E4B\uFF0C\u5219\u4E07\u7269\u83AB\u4E0D\u65E0\u3002\u77E5\u4E1C\u897F\u4E4B\u76F8\u53CD\u800C\u4E0D\u53EF\u4EE5\u76F8\u65E0\uFF0C\u5219\u529F\u5206\u5B9A\u77E3\u3002\u4EE5\u8DA3\u89C2\u4E4B\uFF0C\u56E0\u5176\u6240\u7136\u800C\u7136\u4E4B\uFF0C\u5219\u4E07\u7269\u83AB\u4E0D\u7136\uFF1B\u56E0\u5176\u6240\u975E\u800C\u975E\u4E4B\uFF0C\u5219\u4E07\u7269\u83AB\u4E0D\u975E\u3002\u77E5\u5C27\u3001\u6840\u4E4B\u81EA\u7136\u800C\u76F8\u975E\uFF0C\u5219\u8DA3\u64CD\u7779\u77E3\u3002\u6614\u8005\u5C27\u3001\u821C\u8BA9\u800C\u5E1D\uFF0C\u4E4B\u3001\u54D9\u8BA9\u800C\u7EDD\uFF1B\u6C64\u3001\u6B66\u4E89\u800C\u738B\uFF0C\u767D\u516C\u4E89\u800C\u706D\u3002\u7531\u6B64\u89C2\u4E4B\uFF0C\u4E89\u8BA9\u4E4B\u793C\uFF0C\u5C27\u3001\u6840\u4E4B\u884C\uFF0C\u8D35\u8D31\u6709\u65F6\uFF0C\u672A\u53EF\u4EE5\u4E3A\u5E38\u4E5F\u3002\u6881\u4E3D\u53EF\u4EE5\u51B2\u57CE\u800C\u4E0D\u53EF\u4EE5\u7A92\u7A74\uFF0C\u8A00\u6B8A\u5668\u4E5F\uFF1B\u9A90\u9AA5\u9A85\u9A9D\u4E00\u65E5\u800C\u9A70\u5343\u91CC\uFF0C\u6355\u9F20\u4E0D\u5982\u72F8\u72CC\uFF0C\u8A00\u6B8A\u6280\u4E5F\uFF1B\u9E31\u9E3A\u591C\u64AE\u86A4\uFF0C\u5BDF\u6BEB\u672B\uFF0C\u663C\u51FA\u778B\u76EE\u800C\u4E0D\u89C1\u4E18\u5C71\uFF0C\u8A00\u6B8A\u6027\u4E5F\u3002\u6545\u66F0\uFF1A\u76D6\u5E08\u662F\u800C\u65E0\u975E\uFF0C\u5E08\u6CBB\u800C\u65E0\u4E71\u4E4E\uFF1F\u662F\u672A\u660E\u5929\u5730\u4E4B\u7406\uFF0C\u4E07\u7269\u4E4B\u60C5\u4E5F\u3002\u662F\u72B9\u5E08\u5929\u800C\u65E0\u5730\uFF0C\u5E08\u9634\u800C\u65E0\u9633\uFF0C\u5176\u4E0D\u53EF\u884C\u660E\u77E3\uFF01\u7136\u4E14\u8BED\u800C\u4E0D\u820D\uFF0C\u975E\u611A\u5219\u8BEC\u4E5F\uFF01\u5E1D\u738B\u6B8A\u7985\uFF0C\u4E09\u4EE3\u6B8A\u7EE7\u3002\u5DEE\u5176\u65F6\uFF0C\u9006\u5176\u4FD7\u8005\uFF0C\u8C13\u4E4B\u7BE1\u592B\uFF1B\u5F53\u5176\u65F6\uFF0C\u987A\u5176\u4FD7\u8005\uFF0C\u8C13\u4E4B\u4E49\u4E4B\u5F92\u3002\u9ED8\u9ED8\u4E4E\u6CB3\u4F2F\uFF0C\u5973\u6076\u77E5\u8D35\u8D31\u4E4B\u95E8\uFF0C\u5C0F\u5927\u4E4B\u5BB6\uFF01\u201D</p><p>\u6CB3\u4F2F\u66F0\uFF1A\u201C\u7136\u5219\u6211\u4F55\u4E3A\u4E4E\uFF1F\u4F55\u4E0D\u4E3A\u4E4E\uFF1F\u543E\u8F9E\u53D7\u8DA3\u820D\uFF0C\u543E\u7EC8\u5948\u4F55\uFF1F\u201D</p><p>\u5317\u6D77\u82E5\u66F0\uFF1A\u201C\u4EE5\u9053\u89C2\u4E4B\uFF0C\u4F55\u8D35\u4F55\u8D31\uFF0C\u662F\u8C13\u53CD\u884D\uFF1B\u65E0\u62D8\u800C\u5FD7\uFF0C\u4E0E\u9053\u5927\u8E47\u3002\u4F55\u5C11\u4F55\u591A\uFF0C\u662F\u8C13\u8C22\u65BD\uFF1B\u65E0\u4E00\u800C\u884C\uFF0C\u4E0E\u9053\u53C2\u5DEE\u3002\u4E25\u4E4E\u82E5\u56FD\u4E4B\u6709\u541B\uFF0C\u5176\u65E0\u79C1\u5FB7\uFF1B\u7E47\u7E47\u4E4E\u82E5\u796D\u4E4B\u6709\u793E\uFF0C\u5176\u65E0\u79C1\u798F\uFF1B\u6CDB\u6CDB\u4E4E\u5176\u82E5\u56DB\u65B9\u4E4B\u65E0\u7A77\uFF0C\u5176\u65E0\u6240\u755B\u57DF\u3002\u517C\u6000\u4E07\u7269\uFF0C\u5176\u5B70\u627F\u7FFC\uFF1F\u662F\u8C13\u65E0\u65B9\u3002\u4E07\u7269\u4E00\u9F50\uFF0C\u5B70\u77ED\u5B70\u957F\uFF1F\u9053\u65E0\u7EC8\u59CB\uFF0C\u7269\u6709\u6B7B\u751F\uFF0C\u4E0D\u6043\u5176\u6210\u3002\u4E00\u865A\u4E00\u6EE1\uFF0C\u4E0D\u4F4D\u4E4E\u5176\u5F62\u3002\u5E74\u4E0D\u53EF\u4E3E\uFF0C\u65F6\u4E0D\u53EF\u6B62\u3002\u6D88\u606F\u76C8\u865A\uFF0C\u7EC8\u5219\u6709\u59CB\u3002\u662F\u6240\u4EE5\u8BED\u5927\u4E49\u4E4B\u65B9\uFF0C\u8BBA\u4E07\u7269\u4E4B\u7406\u4E5F\u3002\u7269\u4E4B\u751F\u4E5F\uFF0C\u82E5\u9AA4\u82E5\u9A70\u3002\u65E0\u52A8\u800C\u4E0D\u53D8\uFF0C\u65E0\u65F6\u800C\u4E0D\u79FB\u3002\u4F55\u4E3A\u4E4E\uFF0C\u4F55\u4E0D\u4E3A\u4E4E\uFF1F\u592B\u56FA\u5C06\u81EA\u5316\u3002\u201D</p><p>\u6CB3\u4F2F\u66F0\uFF1A\u201C\u7136\u5219\u4F55\u8D35\u4E8E\u9053\u90AA\uFF1F \u201D</p><p>\u5317\u6D77\u82E5\u66F0\uFF1A\u201C\u77E5\u9053\u8005\u5FC5\u8FBE\u4E8E\u7406\uFF0C\u8FBE\u4E8E\u7406\u8005\u5FC5\u660E\u4E8E\u6743\uFF0C\u660E\u4E8E\u6743\u8005\u4E0D\u4EE5\u7269\u5BB3\u5DF1\u3002\u81F3\u5FB7\u8005\uFF0C\u706B\u5F17\u80FD\u70ED\uFF0C\u6C34\u5F17\u80FD\u6EBA\uFF0C\u5BD2\u6691\u5F17\u80FD\u5BB3\uFF0C\u79BD\u517D\u5F17\u80FD\u8D3C\u3002\u975E\u8C13\u5176\u8584\u4E5F\uFF0C\u8A00\u5BDF\u4E4E\u5B89\u5371\uFF0C\u5B81\u4E8E\u7978\u798F\uFF0C\u8C28\u4E8E\u53BB\u5C31\uFF0C\u83AB\u4E4B\u80FD\u5BB3\u4E5F\u3002\u6545\u66F0\uFF1A\u2018\u5929\u5728\u5185\uFF0C\u4EBA\u5728\u5916\uFF0C\u5FB7\u5728\u4E4E\u5929\u3002\u2019\u77E5\u5929\u4EBA\u4E4B\u884C\uFF0C\u672C\u4E4E\u5929\uFF0C\u4F4D\u4E4E\u5F97\uFF0C\u8E2F\u8E85\u800C\u5C48\u4F38\uFF0C\u53CD\u8981\u800C\u8BED\u6781\u3002\u201D\u66F0\uFF1A\u201C\u4F55\u8C13\u5929\uFF1F\u4F55\u8C13\u4EBA\uFF1F\u201D\u5317\u6D77\u82E5\u66F0\uFF1A\u201C\u725B\u9A6C\u56DB\u8DB3\uFF0C\u662F\u8C13\u5929\uFF1B\u843D\u9A6C\u9996\uFF0C\u7A7F\u725B\u9F3B\uFF0C\u662F\u8C13\u4EBA\u3002\u6545\u66F0\uFF1A\u2018\u65E0\u4EE5\u4EBA\u706D\u5929\uFF0C\u65E0\u4EE5\u6545\u706D\u547D\uFF0C\u65E0\u4EE5\u5F97\u6B89\u540D\u3002\u8C28\u5B88\u800C\u52FF\u5931\uFF0C\u662F\u8C13\u53CD\u5176\u771F\u3002\u2019\u201D</p><h2 id="\u86C7\u4E0E\u98CE" tabindex="-1">\u86C7\u4E0E\u98CE <a class="header-anchor" href="#\u86C7\u4E0E\u98CE" aria-label="Permalink to &quot;\u86C7\u4E0E\u98CE&quot;">\u200B</a></h2><p>\u5914\u601C\u86BF\uFF0C\u86BF\u601C\u86C7\uFF0C\u86C7\u601C\u98CE\uFF0C\u98CE\u601C\u76EE\uFF0C\u76EE\u601C\u5FC3\u3002</p><p>\u5914\u8C13\u86BF\u66F0\uFF1A\u201C\u543E\u4EE5\u4E00\u8DB3\u8DBB\u8E14\u800C\u884C\uFF0C\u4E88\u65E0\u5982\u77E3\u3002\u4ECA\u5B50\u4E4B\u4F7F\u4E07\u8DB3\uFF0C\u72EC\u5948\u4F55\uFF1F\u201C</p><p>\u86BF\u66F0\uFF1A\u201C\u4E0D\u7136\u3002\u5B50\u4E0D\u89C1\u592B\u553E\u8005\u4E4E\uFF1F\u55B7\u5219\u5927\u8005\u5982\u73E0\uFF0C\u5C0F\u8005\u5982\u96FE,\u6742\u800C\u4E0B\u8005\u4E0D\u53EF\u80DC\u6570\u4E5F\u3002\u4ECA\u4E88\u52A8\u543E\u5929\u673A\uFF0C\u800C\u4E0D\u77E5\u5176\u6240\u4EE5\u7136\u3002\u201D</p><p>\u86BF\u8C13\u86C7\u66F0\uFF1A\u201C\u543E\u4EE5\u4F17\u8DB3\u884C\uFF0C\u800C\u4E0D\u53CA\u5B50\u4E4B\u65E0\u8DB3\uFF0C\u4F55\u4E5F\uFF1F\u201D</p><p>\u86C7\u66F0\uFF1A\u201C\u592B\u5929\u673A\u4E4B\u6240\u52A8\uFF0C\u4F55\u53EF\u6613\u90AA\uFF1F\u543E\u5B89\u7528\u8DB3\u54C9\uFF01\u201D</p><p>\u86C7\u8C13\u98CE\u66F0\uFF1A\u201C\u4E88\u52A8\u543E\u810A\u80C1\u800C\u884C\uFF0C\u5219\u6709\u4F3C\u4E5F\u3002\u4ECA\u5B50\u84EC\u84EC\u7136\u8D77\u4E8E\u5317\u6D77\uFF0C\u84EC\u84EC\u7136\u5165\u4E8E\u5357\u6D77\uFF0C\u800C\u4F3C\u65E0\u6709\uFF0C\u4F55\u4E5F\uFF1F\u201D</p><p>\u98CE\u66F0\uFF1A\u201C\u7136\uFF0C\u4E88\u84EC\u84EC\u7136\u8D77\u4E8E\u5317\u6D77\u800C\u5165\u4E8E\u5357\u6D77\u4E5F\uFF0C\u7136\u800C\u6307\u6211\u5219\u80DC\u6211\uFF0C\u9C0C\u6211\u4EA6\u80DC\u6211\u3002\u867D\u7136\uFF0C\u592B\u6298\u5927\u6728\uFF0C\u871A\u5927\u5C4B\u8005\uFF0C\u552F\u6211\u80FD\u4E5F\u3002\u201D\u6545\u4EE5\u4F17\u5C0F\u4E0D\u80DC\u4E3A\u5927\u80DC\u4E5F\u3002\u4E3A\u5927\u80DC\u8005\uFF0C\u552F\u5723\u4EBA\u80FD\u4E4B\u3002</p><h2 id="\u5B54\u5B50\u4E0E\u5B50\u8DEF" tabindex="-1">\u5B54\u5B50\u4E0E\u5B50\u8DEF <a class="header-anchor" href="#\u5B54\u5B50\u4E0E\u5B50\u8DEF" aria-label="Permalink to &quot;\u5B54\u5B50\u4E0E\u5B50\u8DEF&quot;">\u200B</a></h2><p>\u5B54\u5B50\u6E38\u4E8E\u5321\uFF0C\u5B8B\u4EBA\u56F4\u4E4B\u6570\u531D\uFF0C\u800C\u5F26\u6B4C\u4E0D\u8F8D\u3002</p><p>\u5B50\u8DEF\u5165\u89C1\uFF0C\u66F0\uFF1A\u201C\u4F55\u592B\u5B50\u4E4B\u5A31\u4E5F\uFF1F\u201D</p><p>\u5B54\u5B50\u66F0\uFF1A\u201C\u6765\uFF0C\u543E\u8BED\u5973\u3002\u6211\u8BB3\u7A77\u4E45\u77E3\uFF0C\u800C\u4E0D\u514D\uFF0C\u547D\u4E5F\uFF1B\u6C42\u901A\u4E45\u77E3\uFF0C\u800C\u4E0D\u5F97\uFF0C\u65F6\u4E5F\u3002\u5F53\u5C27\u3001\u821C\u800C\u5929\u4E0B\u65E0\u7A77\u4EBA\uFF0C\u975E\u77E5\u5F97\u4E5F\uFF1B\u5F53\u6840\u3001\u7EA3\u800C\u5929\u4E0B\u65E0\u901A\u4EBA\uFF0C\u975E\u77E5\u5931\u4E5F\uFF1A\u65F6\u52BF\u9002\u7136\u3002\u592B\u6C34\u884C\u4E0D\u907F\u86DF\u9F99\u8005\uFF0C\u6E14\u7236\u4E4B\u52C7\u4E5F\uFF1B\u9646\u884C\u4E0D\u907F\u5155\u864E\u8005\uFF0C\u730E\u592B\u4E4B\u52C7\u4E5F\uFF1B\u767D\u5203\u4EA4\u4E8E\u524D\uFF0C\u89C6\u6B7B\u82E5\u751F\u8005\uFF0C\u70C8\u58EB\u4E4B\u52C7\u4E5F\uFF1B\u77E5\u7A77\u4E4B\u6709\u547D\uFF0C\u77E5\u901A\u4E4B\u6709\u65F6\uFF0C\u4E34\u5927\u96BE\u800C\u4E0D\u60E7\u8005\uFF0C\u5723\u4EBA\u4E4B\u52C7\u4E5F\u3002\u7531\uFF0C\u5904\u77E3\uFF01\u543E\u547D\u6709\u6240\u5236\u77E3\uFF01\u201D\u65E0\u51E0\u4F55\uFF0C\u5C06\u7532\u8005\u8FDB\uFF0C\u8F9E\u66F0\uFF1A\u201C\u4EE5\u4E3A\u9633\u864E\u4E5F\uFF0C\u6545\u56F4\u4E4B\uFF1B\u4ECA\u975E\u4E5F\uFF0C\u8BF7\u8F9E\u800C\u9000\u3002\u201D</p><h2 id="\u516C\u5B59\u9F99\u4E0E\u9B4F\u725F" tabindex="-1">\u516C\u5B59\u9F99\u4E0E\u9B4F\u725F <a class="header-anchor" href="#\u516C\u5B59\u9F99\u4E0E\u9B4F\u725F" aria-label="Permalink to &quot;\u516C\u5B59\u9F99\u4E0E\u9B4F\u725F&quot;">\u200B</a></h2><p>\u516C\u5B59\u9F99\u95EE\u4E8E\u9B4F\u725F\u66F0\uFF1A\u201C \u9F99\u5C11\u5B66\u5148\u738B\u4E4B\u9053\uFF0C\u957F\u800C\u660E\u4EC1\u4E49\u4E4B\u884C\uFF1B\u5408\u540C\u5F02\uFF0C\u79BB\u575A\u767D\uFF1B\u7136\u4E0D\u7136\uFF0C\u53EF\u4E0D\u53EF\uFF1B\u56F0\u767E\u5BB6\u4E4B\u77E5\uFF0C\u7A77\u4F17\u53E3\u4E4B\u8FA9\uFF1A\u543E\u81EA\u4EE5\u4E3A\u81F3\u8FBE\u5DF2\u3002\u4ECA\u543E\u95FB\u5E84\u5B50\u4E4B\u8A00\uFF0C\u832B\u7136\u5F02\u4E4B\u3002\u4E0D\u77E5\u8BBA\u4E4B\u4E0D\u53CA\u4E0E\uFF1F\u77E5\u4E4B\u5F17\u82E5\u4E0E\uFF1F\u4ECA\u543E\u65E0\u6240\u5F00\u543E\u5599\uFF0C\u6562\u95EE\u5176\u65B9\u3002\u201D</p><p>\u516C\u5B50\u725F\u9690\u673A\u5927\u606F\uFF0C\u4EF0\u5929\u800C\u7B11\u66F0\uFF1A\u201C\u5B50\u72EC\u4E0D\u95FB\u592B\u57F3\u4E95\u4E4B\u86D9\u4E4E\uFF1F\u8C13\u4E1C\u6D77\u4E4B\u9CD6\u66F0\uFF1A\u2018\u543E\u4E50\u4E0E\uFF01\u51FA\u8DF3\u6881\u4E4E\u4E95\u5E72\u4E4B\u4E0A\uFF0C\u5165\u4F11\u4E4E\u7F3A\u7503\u4E4B\u5D16\u3002\u8D74\u6C34\u5219\u63A5\u814B\u6301\u9890\uFF0C\u8E76\u6CE5\u5219\u6CA1\u8DB3\u706D\u8DD7\u3002\u8FD8\u8677\u87F9\u4E0E\u79D1\u6597\uFF0C\u83AB\u543E\u80FD\u82E5\u4E5F\u3002\u4E14\u592B\u64C5\u4E00\u58D1\u4E4B\u6C34\uFF0C\u800C\u8DE8\u8DF1\u57F3\u4E95\u4E4B\u4E50\uFF0C\u6B64\u4EA6\u81F3\u77E3\u3002\u592B\u5B50\u595A\u4E0D\u65F6\u6765\u5165\u89C2\u4E4E\uFF1F\u2019\u4E1C\u6D77\u4E4B\u9CD6\u5DE6\u8DB3\u672A\u5165\uFF0C\u800C\u53F3\u819D\u5DF2\u7D77\u77E3\u3002\u4E8E\u662F\u9021\u5DE1\u800C\u5374\uFF0C\u544A\u4E4B\u6D77\u66F0\uFF1A\u2018\u592B\u5343\u91CC\u4E4B\u8FDC\uFF0C\u4E0D\u8DB3\u4EE5\u4E3E\u5176\u5927\uFF1B\u5343\u4EDE\u4E4B\u9AD8\uFF0C\u4E0D\u8DB3\u4EE5\u6781\u5176\u6DF1\u3002\u79B9\u4E4B\u65F6\uFF0C\u5341\u5E74\u4E5D\u6F66\uFF0C\u800C\u6C34\u5F17\u4E3A\u52A0\u76CA\uFF1B\u6C64\u4E4B\u65F6\uFF0C\u516B\u5E74\u4E03\u65F1\uFF0C\u800C\u5D16\u4E0D\u4E3A\u52A0\u635F\u3002\u592B\u4E0D\u4E3A\u9877\u4E45\u63A8\u79FB\uFF0C\u4E0D\u4EE5\u591A\u5C11\u8FDB\u9000\u8005\uFF0C\u6B64\u4EA6\u4E1C\u6D77\u4E4B\u5927\u4E50\u4E5F\u3002\u2019\u4E8E\u662F\u57F3\u4E95\u4E4B\u86D9\u95FB\u4E4B\uFF0C\u9002\u9002\u7136\u60CA\uFF0C\u89C4\u89C4\u7136\u81EA\u5931\u4E5F\u3002\u4E14\u592B\u77E5\u4E0D\u77E5\u662F\u975E\u4E4B\u7ADF\uFF0C\u800C\u72B9\u6B32\u89C2\u4E8E\u5E84\u5B50\u4E4B\u8A00\uFF0C\u662F\u72B9\u4F7F\u868A\u8D1F\u5C71\uFF0C\u5546\u86B7\u9A70\u6CB3\u4E5F\uFF0C\u5FC5\u4E0D\u80DC\u4EFB\u77E3\u3002\u4E14\u592B\u77E5\u4E0D\u77E5\u8BBA\u6781\u5999\u4E4B\u8A00\uFF0C\u800C\u81EA\u9002\u4E00\u65F6\u4E4B\u5229\u8005\uFF0C\u662F\u975E\u57F3\u4E95\u4E4B\u86D9\u4E0E\uFF1F\u4E14\u5F7C\u65B9\u8DD0\u9EC4\u6CC9\u800C\u767B\u5927\u7687\uFF0C\u65E0\u5357\u65E0\u5317\uFF0C\u596D\u7136\u56DB\u89E3\uFF0C\u6CA6\u4E8E\u4E0D\u6D4B\uFF1B\u65E0\u4E1C\u65E0\u897F\uFF0C\u59CB\u4E8E\u7384\u51A5\uFF0C\u53CD\u4E8E\u5927\u901A\u3002\u5B50\u4E43\u89C4\u89C4\u7136\u800C\u6C42\u4E4B\u4EE5\u5BDF\uFF0C\u7D22\u4E4B\u4EE5\u8FA9\uFF0C\u662F\u76F4\u7528\u7BA1\u7AA5\u5929\uFF0C\u7528\u9525\u6307\u5730\u4E5F\uFF0C\u4E0D\u4EA6\u5C0F\u4E4E\uFF1F\u5B50\u5F80\u77E3\uFF01\u4E14\u5B50\u72EC\u4E0D\u95FB\u592B\u5BFF\u9675\u4F59\u5B50\u4E4B\u5B66\u4E8E\u90AF\u90F8\u4E0E\uFF1F\u672A\u5F97\u56FD\u80FD\uFF0C\u53C8\u5931\u5176\u6545\u884C\u77E3\uFF0C\u76F4\u530D\u5310\u800C\u5F52\u8033\u3002\u4ECA\u5B50\u4E0D\u53BB\uFF0C\u5C06\u5FD8\u5B50\u4E4B\u6545\uFF0C\u5931\u5B50\u4E4B\u4E1A\u3002\u201D</p><p>\u516C\u5B59\u9F99\u53E3\u547F\u800C\u4E0D\u5408\uFF0C\u820C\u4E3E\u800C\u4E0D\u4E0B\uFF0C\u4E43\u9038\u800C\u8D70\u3002</p><h2 id="\u5E84\u5B50\u4E0E\u695A\u81E3" tabindex="-1">\u5E84\u5B50\u4E0E\u695A\u81E3 <a class="header-anchor" href="#\u5E84\u5B50\u4E0E\u695A\u81E3" aria-label="Permalink to &quot;\u5E84\u5B50\u4E0E\u695A\u81E3&quot;">\u200B</a></h2><p>\u5E84\u5B50\u9493\u4E8E\u6FEE\u6C34\u3002\u695A\u738B\u4F7F\u5927\u592B\u4E8C\u4EBA\u5F80\u5148\u7109\uFF0C\u66F0\uFF1A\u201C\u613F\u4EE5\u5883\u5185\u7D2F\u77E3\uFF01\u201D</p><p>\u5E84\u5B50\u6301\u7AFF\u4E0D\u987E\uFF0C\u66F0\uFF1A\u201C\u543E\u95FB\u695A\u6709\u795E\u9F9F\uFF0C\u6B7B\u5DF2\u4E09\u5343\u5C81\u77E3\u3002\u738B\u4EE5\u5DFE\u7B25\u800C\u85CF\u4E4B\u5E99\u5802\u4E4B\u4E0A\u3002\u6B64\u9F9F\u8005\uFF0C\u5B81\u5176\u6B7B\u4E3A\u7559\u9AA8\u800C\u8D35\u4E4E\uFF1F\u5B81\u5176\u751F\u800C\u66F3\u5C3E\u4E8E\u6D82\u4E2D\u4E4E\uFF1F\u201D</p><p>\u4E8C\u5927\u592B\u66F0\uFF1A\u201C\u5B81\u751F\u800C\u66F3\u5C3E\u6D82\u4E2D\u3002\u201D\u5E84\u5B50\u66F0\uFF1A\u201C\u5F80\u77E3\uFF01\u543E\u5C06\u66F3\u5C3E\u4E8E\u6D82\u4E2D\u3002\u201D</p><h2 id="\u5E84\u5B50\u4E0E\u60E0\u5B50" tabindex="-1">\u5E84\u5B50\u4E0E\u60E0\u5B50 <a class="header-anchor" href="#\u5E84\u5B50\u4E0E\u60E0\u5B50" aria-label="Permalink to &quot;\u5E84\u5B50\u4E0E\u60E0\u5B50&quot;">\u200B</a></h2><p>\u60E0\u5B50\u76F8\u6881\uFF0C\u5E84\u5B50\u5F80\u89C1\u4E4B\u3002\u6216\u8C13\u60E0\u5B50\u66F0\uFF1A\u201C\u5E84\u5B50\u6765\uFF0C\u6B32\u4EE3\u5B50\u76F8\u3002\u201D\u4E8E\u662F\u60E0\u5B50\u6050\uFF0C\u641C\u4E8E\u56FD\u4E2D\u4E09\u65E5\u4E09\u591C\u3002\u5E84\u5B50\u5F80\u89C1\u4E4B\uFF0C\u66F0\uFF1A\u201C\u5357\u65B9\u6709\u9E1F\uFF0C\u5176\u540D\u4E3A\u9E53\u9DB5\uFF0C\u5B50\u77E5\u4E4B\u4E4E\uFF1F\u592B\u9E53\u9DB5\u53D1\u4E8E\u5357\u6D77\u800C\u98DE\u4E8E\u5317\u6D77\uFF0C\u975E\u68A7\u6850\u4E0D\u6B62\uFF0C\u975E\u7EC3\u5B9E\u4E0D\u98DF,\u975E\u91B4\u6CC9\u4E0D\u996E\u3002\u4E8E\u662F\u9E31\u5F97\u8150\u9F20\uFF0C\u9E53\u9DB5\u8FC7\u4E4B\uFF0C\u4EF0\u800C\u89C6\u4E4B\u66F0\uFF1A\u2018\u5413\uFF01\u2019\u4ECA\u5B50\u6B32\u4EE5\u5B50\u4E4B\u6881\u56FD\u800C\u5413\u6211\u90AA\uFF1F\u201D</p><p>\u5E84\u5B50\u4E0E\u60E0\u5B50\u6E38\u4E8E\u6FE0\u6881\u4E4B\u4E0A\u3002</p><p>\u5E84\u5B50\u66F0\uFF1A\u201C\u9BC8\u9C7C\u51FA\u6E38\u4ECE\u5BB9\uFF0C\u662F\u9C7C\u4E4B\u4E50\u4E5F\u3002\u201D</p><p>\u60E0\u5B50\u66F0\u2236\u201C\u5B50\u975E\u9C7C\uFF0C\u5B89\u77E5\u9C7C\u4E4B\u4E50\uFF1F\u201D</p><p>\u5E84\u5B50\u66F0\uFF1A\u201C\u5B50\u975E\u6211\uFF0C\u5B89\u77E5\u6211\u4E0D\u77E5\u9C7C\u4E4B\u4E50\uFF1F\u201D</p><p>\u60E0\u5B50\u66F0\uFF1A\u201C\u6211\u975E\u5B50\uFF0C\u56FA\u4E0D\u77E5\u5B50\u77E3\uFF1B\u5B50\u56FA\u975E\u9C7C\u4E5F\uFF0C\u5B50\u4E4B\u4E0D\u77E5\u9C7C\u4E4B\u4E50\u5168\u77E3\uFF01\u201D</p><p>\u5E84\u5B50\u66F0\uFF1A\u201C\u8BF7\u5FAA\u5176\u672C\u3002\u5B50\u66F0\u2018\u6C5D\u5B89\u77E5\u9C7C\u4E50\u2019\u4E91\u8005\uFF0C\u65E2\u5DF2\u77E5\u543E\u77E5\u4E4B\u800C\u95EE\u6211\u3002\u6211\u77E5\u4E4B\u6FE0\u4E0A\u4E5F\u3002\u201D</p></div>',1),i=[o];function h(l,n){return a(),e("div",null,i)}const s=p(r,[["render",h],["__file","2.html.vue"]]);export{s as default};
